document.addEventListener('DOMContentLoaded', () => {
    const linhasAlunos = document.querySelectorAll('.linha-aluno');

    linhasAlunos.forEach(linha => {
        linha.querySelector('.apagar').addEventListener('click', (event) => {
            event.preventDefault();

            const dados = linha.dataset;
            const alunoId = dados.id;

            // Enviar a requisição de exclusão com ID do aluno
            fetch('/academy/assets/php/cadastros/apagaraluno.php', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json' // Especifica que os dados são enviados em JSON
                },
                body: JSON.stringify({ id: alunoId }) // Dados enviados no corpo da requisição como JSON
            })
            .then(response => response.json()) // Espera a resposta em JSON
            .then(data => {
                document.getElementById('popup-message').innerHTML = data.message;
                document.getElementById('popupalunos').classList.add('show');
                document.getElementById('overlay').classList.add('show');
                setTimeout(function() {
                    closePopup();
                    if (data.success) {
                        location.reload(); 
                    }
                }, 1600);
            })
            .catch(error => console.error('Erro:', error));
        });

    });
});
